using System.Collections;using System.Collections.Generic;using UnityEngine;using Random = UnityEngine.Random;public class Enemy : MovingObject {    // amount of time until pet HAS to change direction    public float chargeCooldownMax = 3f;    private float chargeCooldown;	protected override void Start () {        chargeCooldown = Random.Range(0, chargeCooldownMax);        base.Start();	}	    public void MovePet(int xDir, int yDir) {        RaycastHit2D hit;        bool canMove = Move(xDir, yDir, out hit);    }	// Update is called once per frame	void Update () {        if (chargeCooldown > 0) {            chargeCooldown -= Time.deltaTime;        } else {            chargeCooldown = Random.Range(0, chargeCooldownMax);        }			}}