using System.Collections;using System.Collections.Generic;using UnityEngine;using Random = UnityEngine.Random;public class PetController : MovingObject {    // amount of time until pet HAS to change direction    public float chargeCooldownMax = 3f;    private float chargeCooldown;	protected override void Start () {        ChargeMove();        base.Start();	}    private void ChargeMove() {
        chargeCooldown = Random.Range(0, chargeCooldownMax);    }	    public void MovePet(int xDir, int yDir) {        RaycastHit2D hit;        Move(xDir, yDir, out hit);    }	// Update is called once per frame	void Update () {        if (chargeCooldown > 0) {            chargeCooldown -= Time.deltaTime;        } else {            ChargeMove();        }			}}