using System.Collections;using System.Collections.Generic;using UnityEngine;using Random = UnityEngine.Random;public class PetController : MovingObject {    // amount of time until pet HAS to change direction    public float chargeCooldownMax = 1f;    private float chargeCooldown;    private int x = 0;    private int y = 0;	protected override void Start () {        ChargeMove();        base.Start();	}    private void ChargeMove() {        chargeCooldown = Random.Range(0, chargeCooldownMax);        x = Random.Range(-1, 2);        y = Random.Range(-1, 2);        if (x != 0) {            y = 0;        }    }		// Update is called once per frame	void Update () {        if (chargeCooldown >= 0) {            chargeCooldown -= Time.deltaTime;            if (CanMove()) {                RaycastHit2D hit;                Move(x, y, out hit);
            }        } else {            ChargeMove();        }	}}